(function(){tinymce.PluginManager.requireLangPack('pages');tinymce.create('tinymce.plugins.PagesPlugin',{init:function(ed,url){ed.addCommand('mcePages',function(){ed.windowManager.open({file:url+'/dialog.php',width:320+parseInt(ed.getLang('pages.delta_width',0)),height:220+parseInt(ed.getLang('pages.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('pages',{title:'pages.desc',cmd:'mcePages',image:url+'/img/pages.gif'});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'Pages plugin',author:'GodMod',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/pages',version:"1.0"};}});tinymce.PluginManager.add('pages',tinymce.plugins.PagesPlugin);})();